import React, { useState, useEffect, useRef } from 'react';
import './AvailableTimesForm.css';
import supabase from '../supabaseClient';

const isValidDate = (date) => {
    return !isNaN(Date.parse(date));
};

const isValidTime = (time) => {
    const timeRegex = /^([01]\d|2[0-3]):([0-5]\d)$/;
    return timeRegex.test(time);
};

const formatTime = (time) => {
    // Ensure the time is in HH:mm format
    const timeString = time && time.includes(':') ? time : '00:00';
    return new Date(`1970-01-01T${timeString}:00`).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
};

const AvailableTimesForm = ({ onSaveTimes, onDeleteTimes, availableTimes, selectedDate }) => {
    const [times, setTimes] = useState([]);
    const [timeInput, setTimeInput] = useState('');
    const [templates, setTemplates] = useState([]);
    const [templateName, setTemplateName] = useState('');
    const [selectedTemplate, setSelectedTemplate] = useState('');
    const [menuTemplate, setMenuTemplate] = useState(null);
    const [showConfirmation, setShowConfirmation] = useState(false);
    const [templateToDelete, setTemplateToDelete] = useState(null);
    const [errors, setErrors] = useState([]);
    const menuRef = useRef(null);

    useEffect(() => {
        const fetchTemplates = async () => {
            try {
                const { data, error } = await supabase
                    .from('time-templates')
                    .select('*');

                if (error) throw error;

                setTemplates(data || []);
            } catch (error) {
                console.error('Error fetching templates:', error);
            }
        };

        fetchTemplates();
    }, []);

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (menuRef.current && !menuRef.current.contains(event.target)) {
                setMenuTemplate(null);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);

    const handleAddTime = () => {
        const newErrors = [];

        if (!isValidDate(selectedDate)) {
            newErrors.push('Invalid date.');
        }

        if (!isValidTime(timeInput)) {
            newErrors.push('Invalid time format.');
        }

        if (newErrors.length > 0) {
            setErrors(newErrors);
            return;
        }

        setErrors([]);
        setTimes([...times, { date: selectedDate, time: timeInput }]);
        setTimeInput('');
    };

    const handleSave = async () => {
        if (times.length === 0) {
            alert('No times to save.');
            return;
        }

        const invalidTimes = times.filter(time => !isValidDate(time.date) || !isValidTime(time.time));

        if (invalidTimes.length > 0) {
            setErrors(['Some time entries are invalid and will not be saved.']);
            return;
        }

        try {
            const formattedTimes = times.map(time => ({
                date: time.date,
                time: time.time
            }));

            const { data, error } = await supabase
                .from('available_times')
                .upsert(formattedTimes, { returning: 'representation' });

            if (error) {
                console.error('Supabase error:', error);
                throw error;
            }

            setTimes([]);
            setErrors([]);
        } catch (error) {
            console.error('Error saving times:', error);
            setErrors(['An error occurred while saving times.']);
        }
    };

    const handleSaveTemplate = async () => {
        if (templateName && times.length) {
            const invalidTimes = times.filter(time => !isValidDate(time.date) || !isValidTime(time.time));

            if (invalidTimes.length > 0) {
                setErrors(['Some time entries are invalid and will not be saved.']);
                return;
            }

            try {
                const newTemplate = { name: templateName, times: JSON.stringify(times) };
                const { error } = await supabase
                    .from('time-templates')
                    .insert([newTemplate]);

                if (error) throw error;

                const { data, error: fetchError } = await supabase
                    .from('time-templates')
                    .select('*');

                if (fetchError) throw fetchError;

                setTemplates(data || []);
                setTemplateName('');
                setTimes([]);
                setErrors([]);
            } catch (error) {
                console.error('Error saving template:', error);
            }
        } else {
            alert('Template name or times cannot be empty');
        }
    };

    const handleSelectTemplate = async (e) => {
        const selected = e.target.value;
        setSelectedTemplate(selected);

        if (selected) {
            try {
                const { data, error } = await supabase
                    .from('time-templates')
                    .select('times')
                    .eq('name', selected)
                    .single();

                if (error) throw error;

                const template = data;
                if (template) {
                    setTimes(JSON.parse(template.times));
                }
            } catch (error) {
                console.error('Error fetching template:', error);
            }
        } else {
            setTimes([]);
        }
    };

    const handleDeleteTemplate = async () => {
        if (templateToDelete) {
            try {
                const { error } = await supabase
                    .from('time-templates')
                    .delete()
                    .eq('name', templateToDelete);

                if (error) throw error;

                const { data, error: fetchError } = await supabase
                    .from('time-templates')
                    .select('*');

                if (fetchError) throw fetchError;

                setTemplates(data || []);
                setShowConfirmation(false);
                setTemplateToDelete(null);
            } catch (error) {
                console.error('Error deleting template:', error);
            }
        }
    };

    const handleConfirmDelete = () => {
        handleDeleteTemplate();
    };

    const handleActionClick = (templateName) => {
        setMenuTemplate(templateName);
        setShowConfirmation(false);
    };

    const handleMenuAction = (action) => {
        if (action === 'Delete') {
            setTemplateToDelete(menuTemplate);
            setShowConfirmation(true);
        } else if (action === 'Use') {
            const template = templates.find((t) => t.name === menuTemplate);
            if (template) {
                setTimes(JSON.parse(template.times));
            }
            setMenuTemplate(null);
        }
    };

    return (
        <div className="times-form">
            <div className="left-side">
                <div className="form-group">
                    <h4 className="label">Add Times</h4>
                    <input
                        type="time"
                        value={timeInput}
                        onChange={(e) => setTimeInput(e.target.value)}
                        placeholder="Enter time"
                        className="input"
                    />
                    <button className="add-button" onClick={handleAddTime}>Add Time</button>
                </div>

                {errors.length > 0 && (
                    <div className="errors">
                        {errors.map((error, index) => (
                            <div key={index} className="error-message">{error}</div>
                        ))}
                    </div>
                )}

                <div>
                    <h4 className="label">Times to be saved</h4>
                    <ul className="list">
                        {times.map((time, index) => (
                            <li key={index} className="list-item">
                                {formatTime(time.time)}
                                <span className="delete-icon" onClick={() => setTimes(times.filter((_, i) => i !== index))}>Ã—</span>
                            </li>
                        ))}
                    </ul>
                    <button className="save-button" onClick={handleSave}>Save Times</button>
                </div>
                
                <div>
                    <h4 className="label">Active Times in Calendar</h4>
                    <ul className="list">
                        {availableTimes.map((time, index) => (
                            <li key={index} className="list-item">
                                {formatTime(time.time)}
                            </li>
                        ))}
                    </ul>
                </div>
            </div>

            <div className="right-side">
                <div className="template-section">
                    <h4 className="label">Save Template</h4>
                    <input
                        type="text"
                        value={templateName}
                        onChange={(e) => setTemplateName(e.target.value)}
                        placeholder="Enter template name"
                        className="input"
                    />
                    <button className="save-template-button" onClick={handleSaveTemplate}>Save Template</button>
                </div>

                <div className="template-section">
                    <h4 className="label">Load Template</h4>
                    <select value={selectedTemplate} onChange={handleSelectTemplate} className="input">
                        <option value="">Select Template</option>
                        {templates.map((template) => (
                            <option key={template.id} value={template.name}>
                                {template.name}
                            </option>
                        ))}
                    </select>
                </div>

                {showConfirmation && (
                    <div className="confirmation-popup">
                        <p>Are you sure you want to delete this template?</p>
                        <button onClick={handleConfirmDelete}>Yes</button>
                        <button onClick={() => setShowConfirmation(false)}>No</button>
                    </div>
                )}

                {menuTemplate && (
                    <div className="menu" ref={menuRef}>
                        <button onClick={() => handleMenuAction('Use')}>Use</button>
                        <button onClick={() => handleMenuAction('Delete')}>Delete</button>
                    </div>
                )}
            </div>
        </div>
    );
};

export default AvailableTimesForm;
