import React, { useState, useEffect, useCallback } from 'react';
import CalendarComponent from '../components/CalendarComponent';
import supabase from '../supabaseClient'; // Import Supabase client
import './Styling/adminDashboard.css';
import AvailableTimesForm from '../components/AvailableTimesForm';

// Utility function to validate date and time
const isValidDate = (date) => !isNaN(Date.parse(date));
const isValidTime = (time) => /^([01]\d|2[0-3]):([0-5]\d)$/.test(time);

const AdminDashboard = () => {
    const [selectedDate, setSelectedDate] = useState(null);
    const [availableTimes, setAvailableTimes] = useState([]);
    const [bookings, setBookings] = useState([]);
    const [showTimesForm, setShowTimesForm] = useState(false);

    // Fetch available times based on the selected date
    const fetchTimes = useCallback(async () => {
        if (selectedDate) {
            try {
                const { data, error } = await supabase
                    .from('available_times')
                    .select('*')
                    .eq('date', selectedDate.toISOString().split('T')[0]);

                if (error) throw error;

                setAvailableTimes(data || []);
            } catch (error) {
                console.error('Failed to fetch times:', error);
            }
        }
    }, [selectedDate]);

    useEffect(() => {
        fetchTimes();
    }, [fetchTimes]);

    // Fetch all bookings
    const fetchBookings = useCallback(async () => {
        try {
            const { data, error } = await supabase
                .from('bookings')
                .select('*');

            if (error) throw error;

            setBookings(data || []);
        } catch (error) {
            console.error('Error fetching bookings:', error);
        }
    }, []);

    useEffect(() => {
        fetchBookings();
    }, [fetchBookings]);

    // Save times to the database
    const handleSaveTimes = async (times) => {
        if (selectedDate) {
            try {
                const validTimes = times.filter(time => isValidDate(time.date) && isValidTime(time.time));

                if (validTimes.length !== times.length) {
                    alert('Some time entries are invalid and will not be saved.');
                }

                for (const time of validTimes) {
                    const { error } = await supabase
                        .from('available_times')
                        .upsert({ date: selectedDate.toISOString().split('T')[0], ...time });

                    if (error) throw error;
                }

                setAvailableTimes((prevTimes) => [...prevTimes, ...validTimes]);
            } catch (error) {
                console.error('Failed to save times:', error);
            }
        }
    };

    // Delete times from the database
    const handleDeleteTimes = async (timesToDelete) => {
        if (selectedDate) {
            try {
                const { error } = await supabase
                    .from('available_times')
                    .delete()
                    .in('id', timesToDelete.map(time => time.id))
                    .eq('date', selectedDate.toISOString().split('T')[0]);

                if (error) throw error;

                setAvailableTimes((prevTimes) => prevTimes.filter(time => !timesToDelete.some(t => t.id === time.id)));
            } catch (error) {
                console.error('Failed to delete times:', error);
            }
        }
    };

    // Delete a booking from the database
    const handleDeleteBooking = async (bookingId) => {
        if (!bookingId) {
            console.error('No booking ID provided');
            return;
        }

        try {
            const { error } = await supabase
                .from('bookings')
                .delete()
                .eq('id', bookingId);

            if (error) throw error;

            setBookings((prevBookings) => prevBookings.filter((booking) => booking.id !== bookingId));
            alert('Booking deleted successfully');
        } catch (error) {
            console.error('Error deleting booking:', error);
            alert('Error deleting booking');
        }
    };

    return (
        <div className="admin-container">
            <h1 className="heading">Admin Dashboard</h1>
            <CalendarComponent setSelectedDate={setSelectedDate} />

            {selectedDate && (
                <div className="times-section">
                    <button
                        className="subHeading-dropdown-toggle"
                        onClick={() => setShowTimesForm(!showTimesForm)}
                    >
                        <h2 className="subHeading">Manage Times for {selectedDate.toDateString()}</h2>
                    </button>

                    {showTimesForm && (
                        <AvailableTimesForm
                            onSaveTimes={handleSaveTimes}
                            onDeleteTimes={handleDeleteTimes}
                            availableTimes={availableTimes}
                        />
                    )}
                </div>
            )}

            <div className="section">
                <h2 className="subHeading">All Bookings</h2>
                <BookingsTable bookings={bookings} onDeleteBooking={handleDeleteBooking} />
            </div>
        </div>
    );
};

const BookingsTable = ({ bookings, onDeleteBooking }) => (
    <div className="table-responsive">
        <table className="table">
            <thead>
                <tr>
                    <th>Date</th>
                    <th>Time</th>
                    <th>Service</th>
                    <th>User Name</th>
                    <th>User Email</th>
                    <th>User Phone</th>
                    <th>Actions</th>
                </tr>
            </thead>
            <tbody>
                {bookings.map((booking) => (
                    <tr key={booking.id}>
                        <td>{booking.date}</td>
                        <td>{booking.time}</td>
                        <td>{booking.service}</td>
                        <td>{booking.user_name}</td>
                        <td>{booking.user_email}</td>
                        <td>{booking.user_phone}</td>
                        <td>
                            <button
                                className="delete-button"
                                onClick={() => onDeleteBooking(booking.id)}
                            >
                                Cancel Booking
                            </button>
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    </div>
);

export default AdminDashboard;
