import React, { useState, useEffect } from 'react';
import './BookingsComponent.css'; // Assuming the styles are imported from a CSS file

const BookingsComponent = ({ bookings }) => {
    const [upcomingBookings, setUpcomingBookings] = useState([]);

    useEffect(() => {
        // Get the upcoming bookings on component mount
        const filteredBookings = getUpcomingBookings(bookings);
        setUpcomingBookings(filteredBookings);
    }, [bookings]); // Re-run if bookings prop changes

    // Function to filter upcoming bookings
    function getUpcomingBookings(bookings) {
        const currentDate = new Date(); // Get the current date and time

        return bookings.filter(booking => {
            // Combine the booking date and time into a single Date object
            const bookingDateTime = new Date(`${booking.date}T${booking.time}:00`);

            // Return only bookings where the booking date and time is in the future
            return bookingDateTime > currentDate;
        });
    }

    return (
        <div className="section">
            <div className="header">
                <h2 className="subHeading">Upcoming Bookings</h2>
                <button className="refresh-button" onClick={() => setUpcomingBookings(getUpcomingBookings(bookings))}>
                    Refresh
                </button>
            </div>

            <div className="filters">
                {/* Add filter options or any other UI elements if needed */}
            </div>

            <div className="table-responsive">
                <table className="table">
                    <thead>
                        <tr>
                            <th>Service</th>
                            <th>Date</th>
                            <th>Time</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {upcomingBookings.length > 0 ? (
                            upcomingBookings.map(booking => (
                                <tr key={booking.id}>
                                    <td>{booking.service}</td>
                                    <td>{booking.date}</td>
                                    <td>{booking.time}</td>
                                    <td>
                                        <button className="delete-button">
                                            Delete
                                        </button>
                                    </td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan="4" style={{ textAlign: 'center' }}>
                                    No upcoming bookings.
                                </td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default BookingsComponent;
