import React, { useState, useEffect } from 'react';
import supabase from '../supabaseClient'; // Use default import for supabase
import Calendar from 'react-calendar'; // Make sure this package is installed
import './Styling/dashboardStyle.css'; // Adjust the path to your custom CSS file

const Dashboard = ({ user }) => {
    // State variables
    const [date, setDate] = useState(new Date());
    const [availableTimes, setAvailableTimes] = useState([]);
    const [selectedTime, setSelectedTime] = useState('');
    const [service, setService] = useState('');
    const [userName, setUserName] = useState('');
    const [userEmail, setUserEmail] = useState('');
    const [userPhone, setUserPhone] = useState('');
    const [bookingId, setBookingId] = useState(null);

    const testBooking = async () => {
        try {
            console.log('Attempting to insert test booking'); // Debug statement
            const { data, error } = await supabase
                .from('bookings')
                .insert([{
                    date: new Date().toDateString(),
                    time: '12:00 PM',
                    service: 'Test Service',
                    'user_name': 'Test User',
                    'user_email': 'test@example.com',
                    'user_phone': '123-456-7890',
                }]);
    
            console.log('Test Booking Data:', data);
            console.error('Test Booking Error:', error);
    
            if (error) {
                console.error('Error during test booking:', error.message);
                alert('Test booking failed.');
            } else {
                console.log('Test booking successful, data:', data);
                alert('Test booking successful!');
            }
        } catch (err) {
            console.error('Error during test booking:', err.message);
            alert('Error during test booking.');
        }
    };
    

    // Fetch available times whenever the date changes
    useEffect(() => {
        const fetchAvailableTimes = async () => {
            try {
                const { data, error } = await supabase
                    .from('available_times')
                    .select('id, time') // Fetch the ID along with the time
                    .eq('date', date.toDateString());

                if (error) {
                    console.error('Error fetching available times:', error);
                } else {
                    setAvailableTimes(data.map(item => ({ id: item.id, time: item.time })));
                }
            } catch (error) {
                console.error('Error fetching available times:', error);
            }
        };

        fetchAvailableTimes();
    }, [date]);

    const handleBooking = async () => {
        console.log('handleBooking function called');
    
        if (!selectedTime || !service || !userName || !userEmail || !userPhone) {
            console.log('Missing fields');
            alert('Please fill in all fields before booking.');
            return;
        }
    
        console.log('Current State before booking:', {
            selectedTime,
            service,
            userName,
            userEmail,
            userPhone,
            bookingId,
            availableTimes
        });
    
        try {
            console.log('Attempting to insert booking');
            const { data: bookingData, error: bookingError } = await supabase
                .from('bookings')
                .insert([{
                    date: date.toISOString().split('T')[0], // Format date as YYYY-MM-DD
                    time: selectedTime,
                    service,
                    user_name: userName,    
                    user_email: userEmail,
                    user_phone: userPhone
                }]);
    
            console.log('Booking Data:', bookingData);
            console.log('Booking Error:', bookingError);
    
            if (bookingError) {
                console.error('Error during booking:', bookingError.message);
                alert('Booking failed. Please try again.');
                return;
            }
    
            if (bookingData && bookingData.length > 0) {
                // Successful booking
                alert('Booking successful!');
                setBookingId(bookingData[0]?.id);
    
                // Remove the booked time from available times
                const bookedTime = availableTimes.find(time => time.time === selectedTime);
    
                if (bookedTime) {
                    console.log('Removing booked time');
                    const { error: deleteError } = await supabase
                        .from('available_times')
                        .delete()
                        .eq('time', bookedTime.time)
                        .eq('date', date.toISOString().split('T')[0]);
    
                    if (deleteError) {
                        console.error('Error removing available time:', deleteError.message);
                    } else {
                        console.log('Available time removed successfully');
                        setAvailableTimes(prevTimes => prevTimes.filter(time => time.time !== selectedTime));
                    }
                }
            } else {
                console.log('No booking data returned');
            }
        } catch (err) {
            console.error('Error making booking:', err.message);
            alert('An error occurred while making the booking.');
        }
    };
    
    
    
    
    
    
    
    
    

    const handleDeleteBooking = async () => {
        if (!bookingId) {
            alert('No booking ID available.');
            return;
        }

        try {
            const { error } = await supabase
                .from('bookings')
                .delete()
                .eq('id', bookingId);

            if (error) {
                alert('Failed to delete booking. Please try again.');
                console.error('Error deleting booking:', error);
            } else {
                alert('Booking deleted successfully');
                setBookingId(null); // Clear booking ID
            }
        } catch (error) {
            console.error('Error deleting booking:', error);
            alert('Error deleting booking. Please try again later.');
        }
    };

    return (
        
        <div>
            <h1>Dashboard</h1>
            <Calendar onChange={setDate} value={date} />
            <div>
                <h2>Available Times for {date.toDateString()}</h2>
                {availableTimes.length > 0 ? (
                    <ul>
                        {availableTimes.map((time, index) => (
                            <li key={index}>
                                <button onClick={() => setSelectedTime(time.time)}>{time.time}</button>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No available times for the selected date.</p>
                )}
            </div>
            {selectedTime && (
                <div>
                    <h3>Book a Reservation for {selectedTime} on {date.toDateString()}</h3>
                    <form onSubmit={(e) => {
                        e.preventDefault();
                        handleBooking();
                    }}>
                        <input
                            type="text"
                            value={userName}
                            onChange={(e) => setUserName(e.target.value)}
                            placeholder="Enter your name"
                            required
                        />
                        <input
                            type="email"
                            value={userEmail}
                            onChange={(e) => setUserEmail(e.target.value)}
                            placeholder="Enter your email"
                            required
                        />
                        <input
                            type="tel"
                            value={userPhone}
                            onChange={(e) => setUserPhone(e.target.value)}
                            placeholder="Enter your phone number"
                            required
                        />
                        <input
                            type="text"
                            value={service}
                            onChange={(e) => setService(e.target.value)}
                            placeholder="Enter service"
                            required
                        />
                        <button type="submit">Book Time</button>
                    </form>
                    {bookingId && (
                        <button onClick={handleDeleteBooking}>Cancel Booking</button>
                    )}
                </div>
                
            )}
        </div>
        
    );
    
};

export default Dashboard;
