import React, { useState, useEffect } from 'react';

// Placeholder for TimePicker component, replace with your actual time picker implementation
const TimePicker = ({ onAddTime }) => {
    const [time, setTime] = useState('');

    const handleTimeChange = (e) => {
        setTime(e.target.value);
    };

    const handleAdd = () => {
        if (time) {
            onAddTime(time);
            setTime(''); // Clear time input after adding
        }
    };

    return (
        <div>
            <input
                type="text"
                value={time}
                onChange={handleTimeChange}
                placeholder="HH:MM"
            />
            <button onClick={handleAdd}>Add Time</button>
        </div>
    );
};

const AvailableTimesForm = ({ onSaveTimes, onDeleteTimes, availableTimes, selectedDate }) => {
    const [timesToSave, setTimesToSave] = useState([]);
    const [selectedTimes, setSelectedTimes] = useState([]);

    useEffect(() => {
        setSelectedTimes(availableTimes);
    }, [availableTimes]);

    const handleAddTime = (time) => {
        setTimesToSave((prev) => [...prev, { time, date: selectedDate }]);
    };

    const handleRemoveTime = (timeToRemove) => {
        setTimesToSave((prev) => prev.filter(time => time !== timeToRemove));
    };

    const handleSave = () => {
        if (timesToSave.length > 0) {
            onSaveTimes(timesToSave);
            setTimesToSave([]); // Clear times after saving
        }
    };

    const handleDelete = (timesToDelete) => {
        onDeleteTimes(timesToDelete);
    };

    return (
        <div>
            <h3>Available Times</h3>
            <div>
                <TimePicker onAddTime={handleAddTime} />
            </div>
            <div>
                <h4>Times to Save</h4>
                <ul>
                    {timesToSave.map((time, index) => (
                        <li key={index}>
                            {time.time}
                            <button onClick={() => handleRemoveTime(time)}>Remove</button>
                        </li>
                    ))}
                </ul>
                <button onClick={handleSave}>Save Times</button>
                <button onClick={() => handleDelete(timesToSave)}>Delete Times</button>
            </div>
        </div>
    );
};

export default AvailableTimesForm;
