import React, { useState, useEffect, useCallback } from 'react';
import CalendarComponent from '../components/CalendarComponent';
import supabase from '../supabaseClient'; // Import Supabase client
import './Styling/adminDashboard.css';
import AvailableTimesForm from '../components/AvailableTimesForm';

const AdminDashboard = () => {
    const [selectedDate, setSelectedDate] = useState(null);
    const [availableTimes, setAvailableTimes] = useState([]);
    const [bookings, setBookings] = useState([]);
    const [showTimesForm, setShowTimesForm] = useState(false);

    const fetchTimes = useCallback(async () => {
        if (selectedDate) {
            try {
                const { data, error } = await supabase
                    .from('available-times')
                    .select('*')
                    .eq('date', selectedDate.toISOString().split('T')[0]); // Format date to YYYY-MM-DD

                if (error) throw error;

                setAvailableTimes(data || []);
            } catch (error) {
                console.error('Failed to fetch times:', error);
            }
        }
    }, [selectedDate]);

    useEffect(() => {
        fetchTimes();
    }, [fetchTimes]);

    const fetchBookings = useCallback(async () => {
        try {
            const { data, error } = await supabase
                .from('bookings')
                .select('*');

            if (error) throw error;

            setBookings(data || []);
        } catch (error) {
            console.error('Error fetching bookings:', error);
        }
    }, []);

    useEffect(() => {
        fetchBookings();
    }, [fetchBookings]);

    const handleSaveTimes = async (times) => {
        if (selectedDate) {
            try {
                // Ensure times is an array and date is properly formatted
                if (!Array.isArray(times)) throw new Error('Times should be an array');
                
                const formattedDate = selectedDate.toISOString().split('T')[0]; // Format date to YYYY-MM-DD

                for (const time of times) {
                    const { error } = await supabase
                        .from('available-times')
                        .upsert({ date: formattedDate, ...time });

                    if (error) throw error;
                }

                setAvailableTimes((prevTimes) => [...prevTimes, ...times]);
            } catch (error) {
                console.error('Failed to save times:', error);
            }
        }
    };

    const handleDeleteTimes = async (timesToDelete) => {
        if (selectedDate) {
            try {
                const formattedDate = selectedDate.toISOString().split('T')[0]; // Format date to YYYY-MM-DD

                const { error } = await supabase
                    .from('available-times')
                    .delete()
                    .in('time', timesToDelete)
                    .eq('date', formattedDate);

                if (error) throw error;

                setAvailableTimes((prevTimes) => prevTimes.filter(time => !timesToDelete.includes(time)));
            } catch (error) {
                console.error('Failed to delete times:', error);
            }
        }
    };

    const handleDeleteBooking = async (bookingId) => {
        if (!bookingId) {
            console.error('No booking ID provided');
            return;
        }

        try {
            const { error } = await supabase
                .from('bookings')
                .delete()
                .eq('id', bookingId);

            if (error) throw error;

            setBookings((prevBookings) => prevBookings.filter((booking) => booking.id !== bookingId));
            alert('Booking deleted successfully');
        } catch (error) {
            console.error('Error deleting booking:', error);
            alert('Error deleting booking');
        }
    };

    return (
        <div className="admin-container">
            <h1 className="heading">Admin Dashboard</h1>
            <CalendarComponent setSelectedDate={setSelectedDate} />

            {selectedDate && (
                <div className="times-section">
                    <button
                        className="subHeading-dropdown-toggle"
                        onClick={() => setShowTimesForm(!showTimesForm)}
                    >
                        <h2 className="subHeading">Manage Times for {selectedDate.toDateString()}</h2>
                    </button>

                    {showTimesForm && (
                        <AvailableTimesForm
                            onSaveTimes={handleSaveTimes}
                            onDeleteTimes={handleDeleteTimes}
                            availableTimes={availableTimes}
                        />
                    )}
                </div>
            )}

            <div className="section">
                <h2 className="subHeading">All Bookings</h2>
                <BookingsTable bookings={bookings} onDeleteBooking={handleDeleteBooking} />
            </div>
        </div>
    );
};

const BookingsTable = ({ bookings, onDeleteBooking }) => (
    <div className="table-responsive">
        <table className="table">
            <thead>
                <tr>
                    <th>Date</th>
                    <th>Time</th>
                    <th>Service</th>
                    <th>User Name</th>
                    <th>User Email</th>
                    <th>User Phone</th>
                    <th>Actions</th>
                </tr>
            </thead>
            <tbody>
                {bookings.map((booking) => (
                    <tr key={booking.id}>
                        <td>{booking.date}</td>
                        <td>{booking.time}</td>
                        <td>{booking.service}</td>
                        <td>{booking.userName}</td>
                        <td>{booking.userEmail}</td>
                        <td>{booking.userPhone}</td>
                        <td>
                            <button
                                className="delete-button"
                                onClick={() => onDeleteBooking(booking.id)}
                            >
                                Cancel Booking
                            </button>
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    </div>
);

export default AdminDashboard;
