// src/components/BookingsComponent.js
import React, { useState, useEffect, useCallback } from 'react';
import supabase from '../supabaseClient'; // Import Supabase client

const BookingsComponent = () => {
    const [bookings, setBookings] = useState([]);
    
    const fetchBookings = useCallback(async () => {
        try {
            const { data, error } = await supabase
                .from('bookings')
                .select('*');
    
            if (error) throw error;
    
            console.log('Fetched bookings:', data); // Verify the data structure
    
            setBookings(data || []);
        } catch (error) {
            console.error('Error fetching bookings:', error);
        }
    }, []);
    
    useEffect(() => {
        fetchBookings();
    }, [fetchBookings]);

    const handleDeleteBooking = async (id) => {
        if (window.confirm('Are you sure you want to delete this booking?')) {
            try {
                const { error } = await supabase
                    .from('bookings')
                    .delete()
                    .eq('id', id);

                if (error) throw error;

                setBookings(prevBookings => 
                    prevBookings.filter(booking => booking.id !== id)
                );

                console.log('Booking deleted successfully');
            } catch (error) {
                console.error('Error deleting booking:', error);
            }
        }
    };

    return (
        <div className="section">
            <div className="header">
                <h2 className="subHeading">All Bookings</h2>
                <button className="refresh-button" onClick={fetchBookings}>
                    Refresh
                </button>
            </div>
            <div className="table-responsive">
                <table className="table">
                    <thead>
                        <tr>
                            <th>Date</th>
                            <th>Time</th>
                            <th>Service</th>
                            <th>User Name</th>
                            <th>User Email</th>
                            <th>User Phone</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {bookings.length === 0 ? (
                            <tr>
                                <td colSpan="7">No bookings available</td>
                            </tr>
                        ) : (
                            bookings.map((booking) => (
                                <tr key={booking.id}>
                                    <td>{booking.date || 'N/A'}</td>
                                    <td>{booking.time || 'N/A'}</td>
                                    <td>{booking.service || 'N/A'}</td>
                                    <td>{booking.user_name || 'N/A'}</td>
                                    <td>{booking.user_email || 'N/A'}</td>
                                    <td>{booking.user_phone || 'N/A'}</td>
                                    <td>
                                        <button
                                            className="delete-button"
                                            onClick={() => handleDeleteBooking(booking.id)}
                                        >
                                            Cancel Booking
                                        </button>
                                    </td>
                                </tr>
                            ))
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default BookingsComponent;
